/*cat init.sql | heroku pg:psql postgresql-silhouetted-81810 --app recipe-search-3*/
CREATE DATABASE recipes; 

DROP TABLE demographics,endsurvey,recipes,shownrecipes;

CREATE TABLE recipes(
   recipe_id               VARCHAR(252) NOT NULL PRIMARY KEY
  ,recipe_image_link       VARCHAR(252) NOT NULL
  ,recipe_name             VARCHAR(252) NOT NULL
  ,recipe_description      VARCHAR(484) NOT NULL
  ,recipe_servings         VARCHAR(252) NOT NULL
  ,recipe_directions       VARCHAR(1294) NOT NULL
  ,recipe_directions_cnt   INTEGER NOT NULL
  ,recipe_serving          NUMERIC(5,1) NOT NULL
  ,recipe_calories         VARCHAR(5) NOT NULL
  ,total_gram_weight       NUMERIC(18,13) NOT NULL
  ,avg_rating              NUMERIC(18,16) NOT NULL
  ,number_of_ratings       INTEGER  NOT NULL
  ,kcal_per_100g           NUMERIC(18,14) NOT NULL
  ,protein_per_100g        NUMERIC(19,16) NOT NULL
  ,carbohydrates_per_100g  NUMERIC(19,16) NOT NULL
  ,fat_per_100g            NUMERIC(19,16) NOT NULL
  ,weight_per_serving      NUMERIC(18,14) NOT NULL
  ,weight_per_100          NUMERIC(18,16) NOT NULL
  ,sugar_per_100g          NUMERIC(20,17) NOT NULL
  ,saturated_fat_per_100g  NUMERIC(19,17) NOT NULL
  ,sodium_per_100g         NUMERIC(19,15) NOT NULL
  ,fiber_per_100g          NUMERIC(19,17) NOT NULL
  ,nutri_score             VARCHAR(1) NOT NULL
  ,fat_mtl_score           VARCHAR(6) NOT NULL
  ,saturated_fat_mtl_score VARCHAR(6) NOT NULL
  ,sugar_mtl               VARCHAR(6) NOT NULL
  ,sodium_mtl              VARCHAR(6) NOT NULL
  ,fsa_score               INTEGER  NOT NULL
  ,ingredients             VARCHAR(443) NOT NULL,
  keyword                  VARCHAR(255) NOT NULL
);

CREATE TABLE demographics (
  user_id BIGINT PRIMARY KEY,
  condnum INT,
  iso_time VARCHAR,
  prolific_id VARCHAR,
  gender varchar,
  age INT,
  nationality varchar,
  education varchar,
  experience varchar,
  eating_habits varchar,
  goal text[],
  restrictions text[]
);

CREATE TABLE shownrecipes (
  user_id BIGINT,  
  recipe_id varchar,
  load_more varchar,
  recipe_order varchar, 
  choice_num INT,
  condition INT,
  submitted_at varchar,
  query_term varchar,
  choice_sat_q1 INT,
  choice_sat_q2 INT,
  choice_sat_q3 INT,
  choice_sat_q4 INT,
  choice_sat_q5 INT,
  choice_sat_q6 INT,
  choice_sat_q7 INT,
  variant INT
);

CREATE TABLE endsurvey (
    user_id BIGINT PRIMARY KEY,
    condition INT, 
    submitted_at VARCHAR,
    label_q1 varchar,
    label_q2 varchar,
    label_q3 text[]
);



INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/caribbean-sweet-potato-salad/detail.aspx ','http://images.media-allrecipes.com/userphotos/250x250/00/03/53/35395.jpg','Caribbean Sweet Potato Salad','''This is a 90''s potato salad - a new and improved, redone, much better potato salad. Not only does it have a sweet potato, in addition to a Russet potato, but it also contains corn, cucumber, and peanuts! Corn oil will do if you don''t have canola.''','5 servings','Place the Russet potato pieces into a large saucepan, and cover with salted water. Bring to a boil, turn the heat down, and simmer for 10 minutes. Add the sweet potato, and cook about 15 minutes more. Remove a piece of each potato, and cut it in half to see if it is cooked enough. Once the potatoes are tender, add corn kernels, cook another 30 seconds. Drain through a colander. Fill the saucepan with cold water, and drop vegetables into water. Cool for 5 minutes, and drain.<br>In a large bowl, whisk together mustard, lime juice, cilantro, and garlic. Slowly whisk in oil. Mix in salt and black pepper.<br>Cut cooled potatoes into 1 inch cubes, and add to dressing along with cucumber, and red onion. Toss well. Serve at room temperature or chilled. Toss the peanuts in just before serving.<br>',3,5.0,'231',1362,4.25455,55,84.7639,1.68794,10.201,4.62349,272.521667,2.725217,2.641992,0.440332,106.413557,1.651245,'C','medium','low','low','low',5,'salad','1.russet potato, peeled and quartered,2.sweet potato, peeled and quartered,3.corn,4.prepared Dijon-style mustard,5.fresh lime juice,6.chopped fresh cilantro,7.garlic, minced,8.canola oil,9.salt,10.ground black pepper,11.cucumber, halved lengthwise and chopped,12.red onion, thinly sliced,13.finely chopped peanuts');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/beet-salad-with-goat-cheese/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/11/76/117698.jpg','Beet Salad with Goat Cheese','''This is a delicious and easy salad which takes little time and is a great meatless main course. It uses beets, goat cheese, candied walnuts and baby greens. For a main dish salad, add chicken. Feel free to include more of your favorite vegetables too.''','6 servings','Place beets into a saucepan, and fill with enough water to cover. Bring to a boil, then cook for 20 to 30 minutes, until tender. Drain and cool, then cut in to cubes.<br>While the beets are cooking, place the walnuts in a skillet over medium-low heat. Heat until warm and starting to toast, then stir in the maple syrup. Cook and stir until evenly coated, then remove from the heat and set aside to cool.<br>In a small bowl, whisk together the orange juice concentrate, balsamic vinegar and olive oil to make the dressing.<br>Place a large helping of baby greens onto each of four salad plates, divide candied walnuts equally and sprinkle over the greens. Place equal amounts of beets over the greens, and top with dabs of goat cheese. Drizzle each plate with some of the dressing.<br>',4,6.0,'347',1078.7,4.64865,74,193.01,2.94799,13.9056,14.5175,179.783333,1.797833,11.513859,2.781125,59.516084,1.77992,'D','medium','medium','medium','low',7,'salad','1.beets - scrubbed, trimmed and cut in half,2.chopped walnuts,3.maple syrup,4.mixed baby salad greens,5.frozen orange juice concentrate,6.balsamic vinegar,7.extra-virgin olive oil,8.goat cheese');
