INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,ingredients,keyword) VALUES ('http://allrecipes.com/recipe/pizza-pasta/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/46/14/461444.jpg','Pizza Pasta','''Very easy to make, and kids will love it!''','6 servings','Preheat oven to 350 degrees F (175 degrees C).<br>Bring a large pot of lightly salted water to a boil. Add pasta and cook for 8 to 10 minutes or until al dente, drain.<br>In a medium skillet over medium-high heat, cook beef with onion until beef is brown. Drain. Combine beef mixture with spaghetti sauce, pepperoni and cooked pasta and pour into a 9x13 inch baking dish. Top with mozzarella.<br>Bake in preheated oven for 30 minutes, until cheese is melted and golden.<br>',4,6.0,'513',1870.0,4.42857,112,164.599,10.0107,10.107,9.1123,311.6666666666667,3.1166666666666667,4.010695187165775,3.7540106951871657,360.6417112299465,1.3155080213903745,'D','medium','medium','low','medium',7,'1.rotini pasta,2.lean ground beef,3.onion, diced,4.spaghetti sauce,5.sliced pepperoni sausage,6.shredded mozzarella cheese','pasta');
