INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,ingredients,keyword) VALUES ('http://allrecipes.com/recipe/rich-pasta-for-the-poor-kitchen/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/43/94/439403.jpg','Rich Pasta for the Poor Kitchen','''A quick, simple pasta tossed with butter, parsley, and Parmesan cheese. A wonderful recipe to make when you don''t feel like making a trip to the grocery store. I used what I had on hand and it worked like a charm! Add summer vine-ripened tomatoes for an extra dash of flavor!''','4 servings','Bring a large pot of lightly salted water to a boil. Cook pasta for 8 to 10 minutes, or until al dente, drain.<br>Heat a skillet over medium-low heat. Melt butter with garlic very slowly to avoid burning the garlic. Season with salt, freshly ground black pepper, and parsley. Sprinkle with cayenne pepper to taste, if desired.<br>Toss pasta into the skillet until well-coated with butter. Increase heat to medium, and cook until pasta is heated through and has absorbed some of the butter. Adjust seasoning with salt and pepper, if necessary. Serve with grated Parmesan cheese.<br>',3,4.0,'453',389.12533,4.55102,49,465.66,14.0829,34.7446,30.3244,97.2813325,0.972813325,1.4391250243205702,18.708625316167414,1084.483500470144,1.5419196689148968,'E','high','high','low','medium',9,'1.dry spaghetti,2.butter,3.minced garlic,4.salt and freshly ground black pepper to taste,5.chopped fresh parsley,6.cayenne pepper,7.grated Parmesan cheese','pasta');






INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/chinese-chicken-salad/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/01/01/40/1014047.jpg','Chinese Chicken Salad','''The greatest chicken salad, especially if your clan isn''t crazy about the usual soy flavored dressing.''','6 servings','In a large salad bowl combine the chicken, lettuce, green onion, celery, nuts, seeds and noodles. Mix all together. Set aside.<br>To Make Dressing: Put vinegar in a small bowl. Dissolve sugar and salt in vinegar before adding oil. Shake/Beat well.<br>Add dressing to salad and toss to coat. Serve and enjoy!<br>',3,6.0,'517',1650.0,4.5625,16,188.0,8.69091,14.1091,11.4909,275.0,2.75,5.054545454545455,1.8909090909090909,275.27272727272725,2.327272727272728,'A','medium','medium','medium','low',7,'chicken','1.boneless chicken breast halves, cooked and diced,2.lettuce, torn into small pieces,3.green onions, sliced,4.celery, sliced thin,5.walnuts, chopped,6.sesame seeds, toasted,7.Chinese noodles, heated briefly to crisp,8.seasoned rice vinegar,9.white sugar,10.salt,11.peanut oil');
