INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/hawaiian-chicken-salad/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/21/40/214098.jpg','Hawaiian Chicken Salad','''This delicious, fruity chicken salad is a change from the standard. It''s easy to double the recipe for a larger group.''','6 servings','In a medium bowl, beat cream cheese until fluffy. Mix in salad dressing and 2 tablespoons reserved pineapple juice. Stir in the pineapple tidbits, chicken, almonds, and grapes until evenly coated. Chill until serving.<br>',1,6.0,'416',1247.99999,4.34375,32,200.0,10.4808,9.13462,13.7019,207.9999983333333,2.0799999833333334,7.06730774893676,4.326923111593935,266.8269252149593,1.153846163091716,'B','medium','medium','medium','low',7,'chicken','1.cream cheese, softened,2.creamy salad dressing, e.g. Miracle Whip â¢,3.pineapple tidbits, juice reserved,4.chunk chicken, drained,5.blanched slivered almonds,6.seedless grapes, halved');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/restaurant-style-chicken-nachos/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/86/19/861911.jpg','Restaurant Style Chicken Nachos','''This delicious and easy to prepare recipe is sure to be a big hit! Hearty enough for a meal. The key is the meat filling -- it also makes a great filling for tacos and quesadillas. This recipe can be modified to meet your own taste. I recommend serving with sour cream and guacamole. Be creative!''','6 servings','Preheat oven to 350 degrees F (175 degrees C).<br>In a 12 inch skillet over medium heat, cook and stir the garlic and white parts of the green onions in canola oil until tender. Mix in shredded chicken, salt and pepper. Toss until well coated with oil. Stir in the salsa.<br>Arrange tortilla chips on a large baking sheet. Spoon the chicken mixture over tortilla chips. Top with Cheddar/Monterey Jack cheese blend and tomato. Bake in the preheated oven 10 minutes, or until cheese has melted. Remove from heat and sprinkle with green onion tops before serving.<br>',3,6.0,'407',1100.0,4.57325,157,222.0,11.6727,13.3091,14.0182,183.33333333333331,1.8333333333333333,1.3090909090909089,4.418181818181818,365.45454545454544,1.5272727272727271,'B','medium','medium','low','medium',7,'chicken','1.garlic, crushed,2.green onions, sliced, white parts and tops separated,3.canola oil,4.shredded, cooked, whole chicken breast,5.salt and pepper to taste,6.salsa,7.tortilla chips,8.shredded Cheddar/Monterey Jack cheese blend,9.tomato, diced');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/scrumptious-chicken-vegetable-stew/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/56/94/569403.jpg','Scrumptious Chicken Vegetable Stew','''Low glycemic microwave meal.''','6 servings','Place all ingredients in a large microwave-safe bowl. Stir to combine. Cover, microwave on high power about 25 minutes, or until chicken and vegetables are done. Add more broth if needed. Garnish with parsley.<br>',1,6.0,'**360',2085.04,4.66667,6,103.595,10.3883,8.20128,3.07908,347.50666666666666,3.4750666666666667,1.2086099067643787,0.8345163641944519,75.96976556804665,3.6833825729962015,'B','medium','low','low','low',5,'chicken','1.boneless, skinless chicken thighs or breasts,2.carrots, in 1/2-inch chunks,3.fresh spinach, shredded,4.diced tomatoes with juice,5.dry lentils,6.low-fat chicken broth,7.dried Italian herbs,8.crushed fennel seed,9.garlic cloves, peeled and sliced,10.Salt and pepper, to taste,11.Parsley sprigs for garnish');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/baked-lemon-chicken-with-mushroom-sauce/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/39/21/392151.jpg','Baked Lemon Chicken with Mushroom Sauce','''A great chicken recipe that I have made for years. Serve with rice and a fresh veggie. Impressive!''','6 servings','Preheat oven to 400 degrees F (205 degrees C).<br>Pour olive oil in an 8x8-inch glass baking dish. Place the chicken breasts in the dish, coating each side with oil. Squeeze the juice of 1/2 lemon over each chicken breast. Slice the rest of the lemon and place a lemon slice on top of each chicken piece.<br>Bake in the preheated oven until no longer pink in the center and the juices run clear, 30 to 40 minutes. An instant-read thermometer inserted into the center should read at least 165 degrees F (74 degrees C).<br>Melt butter in a skillet over medium heat, add mushrooms. Cook and stir until mushrooms are brown and liquid is evaporated, about 6 minutes. Sprinkle flour over mushrooms and stir until coated. Add chicken broth, stirring to make a medium-thick sauce. Allow sauce to reduce, adjusting with a little more broth to make a creamy sauce. Add fresh parsley at the last minute. Spoon the sauce over the baked chicken breasts.<br>',4,6.0,'240',1214.625,4.16892,148,118.555,12.9917,2.56869,6.42174,202.4375,2.024375,0.345785736338376,2.9638777400432232,96.82000617474527,0.6421735103426984,'C','medium','medium','low','low',6,'chicken','1.olive oil,2.skinless, boneless chicken breast halves,3.lemon,4.butter,5.fresh sliced mushrooms,6.all-purpose flour,7.chicken broth, or more as needed,8.chopped fresh parsley');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/buffalo-chicken-burgers-with-blue-cheese-dressing/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/02/27/44/2274471.jpg','Buffalo Chicken Burgers with Blue Cheese Dressing','''This burger delivers the bold flavors of Buffalo wings minus the mess and calories. Serve with additional hot sauce on the side.''','4 burgers','Preheat an outdoor grill for medium-high heat, and lightly oil the grate. Stir the sour cream, blue cheese, and Worcestershire sauce together in a small bowl, set aside.<br>Mix the ground chicken, 1/4 cup hot sauce, celery salt, poultry seasoning, paprika, and cayenne pepper together in a mixing bowl until evenly blended. Form into four 3/4-inch-thick patties.<br>Cook the chicken burgers on the preheated grill 6 to 7 minutes until well marked. Spray the uncooked top of the burger with cooking spray, and flip the burgers over. Brush the cooked side with 1 tablespoon hot sauce, and continue cooking until no longer pink in the center and the juices run clear, about 5 minutes more. An instant-read thermometer inserted into the center should read at least 165 degrees F (74 degrees C).<br>To assemble the burgers, place a lettuce leaf on each roll bottom. Top with a burger patty, then spoon on 2 tablespoons of blue cheese sauce, and 2 tablespoons celery. Place the top of the roll on top to finish the sandwich.<br>',4,4.0,'374',1105.5176473,4.54386,57,135.321,16.4629,9.15408,3.18403,276.379411825,2.76379411825,1.049282209861653