INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/cheddar-baked-chicken/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/69/60/696093.jpg','Cheddar Baked Chicken','''This recipe is very quick and easy to prepare. The flavors blend to make it rather tasty. It is great with plain white rice and your favorite vegetable. Why not give it a try?''','6 servings','Preheat oven to 350 degrees F (175 degrees C). Coat a medium baking dish with 1/4 cup melted butter.<br>In a bowl, mix the flour, salt, pepper, and garlic powder. In a separate bowl, beat together the egg and milk. In a third bowl, mix the cheese, bread crumbs, cereal. Dredge chicken pieces in the flour mixture, dip in the egg mixture, then press in the breadcrumb mixture to coat. Arrange in the prepared baking dish. Drizzle 2 tablespoons butter evenly over chicken.<br>Bake 35 minutes in the preheated oven, or until coating is golden brown and chicken juices run clear.<br>',3,6.0,'368',803.716667,4.62921,267,274.724,16.573,14.856,16.4984,133.95277783333333,1.3395277783333333,1.045144432720841,9.704912589550666,646.4964848116059,0.6718785638919692,'E','medium','high','low','medium',8,'chicken','1.butter, melted,2.all-purpose flour,3.salt,4.ground black pepper,5.garlic powder,6.egg,7.milk,8.shredded Cheddar cheese,9.Italian seasoned bread crumbs,10.crispy rice cereal,11.skinless, boneless chicken breast halves - cut in half,12.butter, melted');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/amys-garlic-egg-chicken/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/40/26/402675.jpg','Amy''s Garlic Egg Chicken','''An easy dish for garlic lovers. Golden looking, golden tasting!''','4 servings','In a glass dish, beat egg yolk with garlic. Place chicken in egg mixture, and turn to coat. Cover dish and refrigerate for at least 4 hours, or overnight if possible.<br>Preheat oven to 400 degrees F (200 degrees C).<br>Melt butter and pour into the bottom of a 9x13 inch baking dish. Mix together the bread crumbs, Parmesan cheese, parsley, garlic powder, salt and pepper. Dip marinated chicken in crumb mixture. Place coated chicken in baking dish, and pour remaining egg mixture over.<br>Bake in preheated oven for 15 to 20 minutes on each side, or until chicken is no longer pink and juices run clear.<br>',4,4.0,'508',804.9066940000001,4.49444,180,252.452,18.6357,12.225,14.1631,201.22667350000003,2.0122667350000003,1.2423800267214573,7.901536969948468,776.2390406955665,0.9939040213771658,'E','medium','high','low','medium',8,'chicken','1.egg yolk,2.garlic, chopped,3.skinless, boneless chicken breast halves,4.butter,5.dry bread crumbs,6.grated Parmesan cheese,7.dried parsley,8.garlic powder,9.salt,10.ground black pepper');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/best-baked-chicken/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/60/60/606039.jpg','Best Baked Chicken','''The stuffing mix makes a chewy-crunchy breading, and the combination of seasoning is unusual and delicious''','8 servings','Preheat oven to 375 degrees F (190 degrees C). Lightly grease a baking dish.<br>In a medium bowl, mix the sour cream, lemon juice, soy sauce, celery salt, garlic salt, and pepper. Dip each chicken breast in the sour cream mixture, then roll in the stuffing mix to coat. Arrange chicken in the prepared baking dish. Drizzle with the melted butter.<br>Bake 40 to 50 minutes in the preheated oven, until the chicken is no longer pink and the juices run clear.<br>',3,8.0,'379',1519.19692,4.46154,78,199.579,14.7973,9.58401,11.1111,189.899615,1.89899615,1.0005286213982054,6.319128135146562,733.0188636770011,0.3686158078835493,'E','medium','high','low','medium',8,'chicken','1.skinless, boneless chicken breast halves,2.sour cream,3.fresh lemon juice,4.soy sauce,5.celery salt,6.garlic salt,7.pepper,8.herb-seasoned stuffing mix, crushed into crumbs,9.butter, melted');
INSERT INTO recipes(recipe_id,recipe_image_link,recipe_name,recipe_description,recipe_servings,recipe_directions,recipe_directions_cnt,recipe_serving,recipe_calories,total_gram_weight,avg_rating,number_of_ratings,kcal_per_100g,protein_per_100g,carbohydrates_per_100g,fat_per_100g,weight_per_serving,weight_per_100,sugar_per_100g,saturated_fat_per_100g,sodium_per_100g,fiber_per_100g,nutri_score,fat_mtl_score,saturated_fat_mtl_score,sugar_mtl,sodium_mtl,fsa_score,keyword,ingredients) VALUES ('http://allrecipes.com/recipe/chicken-tikka-masala/detail.aspx','http://images.media-allrecipes.com/userphotos/250x250/00/03/99/39905.jpg','Chicken Tikka Masala','''This is an easy recipe for Chicken Tikka Masala - Chicken marinated in yogurt and spices and then served in a tomato cream sauce. Serve with rice or warm pita bread.''','4 servings','In a large bowl, combine yogurt, lemon juice, 2 teaspoons cumin, cinnamon, cayenne, black pepper, ginger, and 4 teaspoons salt. Stir in chicken, cover, and refrigerate for 1 hour.<br>Preheat a grill for high heat.<br>Lightly oil the grill grate. Thread chicken onto skewers, and discard marinade. Grill until juices run clear, about 5 minutes on each side.<br>Melt butter in a large heavy skillet over medium heat. Saute garlic and jalapeno for 1 minute. Season with 2 teaspoons cumin, paprika, and 3 teaspoons salt. Stir in tomato sauce and cream. Simmer on low heat until sauce thickens, about 20 minutes. Add grilled chicken, and simmer for 10 minutes. Transfer to a serving platter, and garnish with fresh cilantro.<br>',4,4.0,'404',1193.4101260000002,4.43249,474,135.41,8.24528,4.45781,9.68653,298.35253150000005,2.9835253150000005,2.3797351288772286,5.664439954651431,1507.94765419981,0.8379349045342354,'E','medium','high','low','high',9,'chicken','1.yogurt,2.lemon juice,3.ground cumin,4.ground cinnamon,5.cayenne pepper,6.freshly ground black pepper,7.minced fresh ginger,8.salt, or to taste,9.boneless skinless chicken breasts, cut into bite-size pieces,10.long skewers,11.butter,12.garlic, minced,13.jalapeno pepper, finely chopped,14.ground cumin,15.paprika,16.salt, or to taste,17.tomato sauce,18.heavy cream,19.chopped fresh cilantro');
